import React from 'react';
import { useUser } from '../../contexts/UserContext';
import './Header.css';

interface HeaderProps {
  onMenuClick: () => void;
}

const Header: React.FC<HeaderProps> = ({ onMenuClick }) => {
  const { currentUser } = useUser();

  return (
    <header className="header">
      <div className="header-left">
        <button 
          className="menu-button" 
          onClick={onMenuClick}
          aria-label="Toggle menu"
        >
          <div className="hamburger">
            <span></span>
            <span></span>
            <span></span>
          </div>
        </button>
        
        <div className="logo-section">
          <div className="logo">
            <div className="logo-icon">ü§ù</div>
            <span className="logo-text">BuddyMatch</span>
          </div>
          <span className="tagline">Smart Onboarding Companion</span>
        </div>
      </div>

      <div className="header-right">
        <div className="user-info">
          {currentUser ? (
            <>
              <div className="user-avatar">
                {currentUser.firstName[0]}{currentUser.lastName[0]}
              </div>
              <div className="user-details">
                <span className="user-name">{currentUser.fullName}</span>
                <span className="user-role">{currentUser.title}</span>
              </div>
            </>
          ) : (
            <div className="user-placeholder">
              <div className="user-avatar">?</div>
              <div className="user-details">
                <span className="user-name">Guest User</span>
                <span className="user-role">Please select user</span>
              </div>
            </div>
          )}
        </div>
      </div>
    </header>
  );
};

export default Header;
