import React from 'react';
import { NavLink } from 'react-router-dom';
import { useUser } from '../../contexts/UserContext';
import { EmployeeRole } from '../../types';
import './Sidebar.css';

interface SidebarProps {
  isOpen: boolean;
}

const Sidebar: React.FC<SidebarProps> = ({ isOpen }) => {
  const { currentUser } = useUser();

  const navigationItems = [
    {
      path: '/',
      label: 'Dashboard',
      icon: 'üìä',
      roles: [EmployeeRole.HR, EmployeeRole.Employee]
    },
    {
      path: '/employees',
      label: 'Employees',
      icon: 'üë•',
      roles: [EmployeeRole.HR]
    },
    {
      path: '/buddies',
      label: 'Buddy Profiles',
      icon: 'üéØ',
      roles: [EmployeeRole.HR, EmployeeRole.Employee]
    },
    {
      path: '/matching',
      label: 'Smart Matching',
      icon: 'ü§ñ',
      roles: [EmployeeRole.HR]
    },
    {
      path: '/buddy-catalog',
      label: 'Buddy Catalog',
      icon: 'üìö',
      roles: [EmployeeRole.HR]
    },
    {
      path: '/buddy-dashboard',
      label: 'My Buddy Dashboard',
      icon: 'üéØ',
      roles: [EmployeeRole.Employee] // Only for buddy guides
    },
    {
      path: '/messages',
      label: 'Messages',
      icon: 'üí¨',
      roles: [EmployeeRole.HR, EmployeeRole.Employee]
    },
    {
      path: '/gamification',
      label: 'Achievements',
      icon: 'üèÜ',
      roles: [EmployeeRole.HR, EmployeeRole.Employee]
    },
    {
      path: '/analytics',
      label: 'Analytics',
      icon: 'üìà',
      roles: [EmployeeRole.HR]
    },
    {
      path: '/api-test',
      label: 'API Test',
      icon: 'üîß',
      roles: [EmployeeRole.HR, EmployeeRole.Employee]
    },
    {
      path: '/epam-showcase',
      label: 'EPAM Design',
      icon: 'üé®',
      roles: [EmployeeRole.HR, EmployeeRole.Employee]
    }
  ];

  const filteredItems = navigationItems.filter(item => 
    !currentUser || item.roles.includes(currentUser.role)
  );

  return (
    <>
      <div className={`sidebar-overlay ${isOpen ? 'visible' : ''}`} />
      <aside className={`sidebar ${isOpen ? 'open' : ''}`}>
        <div className="sidebar-header">
          <h2 className="sidebar-title">
            <span className="nav-icon">üöÄ</span>
            BuddyMatch
          </h2>
          <p className="sidebar-subtitle">AI-Powered Onboarding</p>
        </div>
        
        <nav className="sidebar-nav">
          <div className="nav-section">
            <h3 className="nav-section-title">Navigation</h3>
            <ul className="nav-list">
              {filteredItems.map((item) => (
                <li key={item.path} className="nav-item">
                  <NavLink
                    to={item.path}
                    className={({ isActive }) =>
                      `nav-link ${isActive ? 'active' : ''}`
                    }
                  >
                    <span className="nav-icon">{item.icon}</span>
                    <span className="nav-text">{item.label}</span>
                    <div className="nav-status"></div>
                  </NavLink>
                </li>
              ))}
            </ul>
          </div>
        </nav>

        {currentUser && (
          <div className="sidebar-footer">
            <div className="sidebar-footer-content">
              <div className="sidebar-version">
                v1.0.0 - Demo
              </div>
              <div className="sidebar-powered">
                Powered by <span className="epam-logo">EPAM</span>
              </div>
            </div>
          </div>
        )}
      </aside>
    </>
  );
};

export default Sidebar;
