import React from 'react';
import { NavLink } from 'react-router-dom';
import { useUser } from '../../contexts/UserContext';
import { EmployeeRole } from '../../types';

interface SidebarProps {
  isOpen: boolean;
}

const Sidebar: React.FC<SidebarProps> = ({ isOpen }) => {
  const { currentUser } = useUser();

  const navigationItems = [
    {
      path: '/',
      label: 'Dashboard',
      icon: 'ðŸ“Š',
      roles: [EmployeeRole.HR, EmployeeRole.Employee]
    },
    {
      path: '/employees',
      label: 'Employees',
      icon: 'ðŸ‘¥',
      roles: [EmployeeRole.HR]
    },
    {
      path: '/buddies',
      label: 'Buddy Profiles',
      icon: 'ðŸŽ¯',
      roles: [EmployeeRole.HR, EmployeeRole.Employee]
    },
    {
      path: '/matching',
      label: 'Smart Matching',
      icon: 'ðŸ¤–',
      roles: [EmployeeRole.HR]
    },
    {
      path: '/buddy-dashboard',
      label: 'My Buddy Dashboard',
      icon: 'ðŸŽ¯',
      roles: [EmployeeRole.Employee] // Only for buddy guides
    },
    {
      path: '/messages',
      label: 'Messages',
      icon: 'ðŸ’¬',
      roles: [EmployeeRole.HR, EmployeeRole.Employee]
    },
    {
      path: '/gamification',
      label: 'Achievements',
      icon: 'ðŸ†',
      roles: [EmployeeRole.HR, EmployeeRole.Employee]
    },
    {
      path: '/analytics',
      label: 'Analytics',
      icon: 'ðŸ“ˆ',
      roles: [EmployeeRole.HR]
    },
    {
      path: '/api-test',
      label: 'API Test',
      icon: 'ðŸ”§',
      roles: [EmployeeRole.HR, EmployeeRole.Employee]
    }
  ];

  const filteredItems = navigationItems.filter(item => 
    !currentUser || item.roles.includes(currentUser.role)
  );

  return (
    <aside className={`sidebar ${isOpen ? 'sidebar-open' : 'sidebar-closed'}`}>
      <nav className="sidebar-nav">
        <ul className="nav-list">
          {filteredItems.map((item) => (
            <li key={item.path} className="nav-item">
              <NavLink
                to={item.path}
                className={({ isActive }) =>
                  `nav-link ${isActive ? 'nav-link-active' : ''}`
                }
              >
                <span className="nav-icon">{item.icon}</span>
                <span className="nav-label">{item.label}</span>
              </NavLink>
            </li>
          ))}
        </ul>
      </nav>

      {currentUser && (
        <div className="sidebar-footer">
          <div className="user-role-badge">
            <span className={`role-indicator ${currentUser.role === EmployeeRole.HR ? 'role-hr' : 'role-employee'}`}>
              {currentUser.role === EmployeeRole.HR ? 'HR' : 'Employee'}
            </span>
          </div>
        </div>
      )}
    </aside>
  );
};

export default Sidebar;
