import React, { useState, useEffect } from 'react';
import { useUser } from '../../contexts/UserContext';
import { Employee, EmployeeRole } from '../../types';
import { employeeApi } from '../../services/api';
import UserSelector from './UserSelector';
import StatsCards from './StatsCards';
import QuickActions from './QuickActions';
import RecentActivity from './RecentActivity';

const Dashboard: React.FC = () => {
  const { currentUser, setCurrentUser } = useUser();
  const [employees, setEmployees] = useState<Employee[]>([]);
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    loadEmployees();
  }, []);

  const loadEmployees = async () => {
    try {
      setLoading(true);
      const data = await employeeApi.getAll();
      setEmployees(data);
    } catch (error) {
      console.error('Failed to load employees:', error);
    } finally {
      setLoading(false);
    }
  };

  const handleUserSelect = (user: Employee) => {
    setCurrentUser(user);
  };

  if (loading) {
    return (
      <div className="dashboard-loading">
        <div className="spinner"></div>
        <p>Loading dashboard...</p>
      </div>
    );
  }

  return (
    <div className="dashboard">
      <div className="dashboard-header">
        <h1 className="dashboard-title">
          Welcome to BuddyMatch
        </h1>
        <p className="dashboard-subtitle">
          Smart onboarding companion system powered by AI
        </p>
      </div>

      {!currentUser ? (
        <div className="user-selection-section">
          <div className="card">
            <div className="card-header">
              <h2 className="card-title">Select Your Profile</h2>
              <p className="card-subtitle">
                Choose your user profile to get started with the demo
              </p>
            </div>
            <UserSelector 
              employees={employees} 
              onUserSelect={handleUserSelect}
            />
          </div>
        </div>
      ) : (
        <div className="dashboard-content">
          <div className="dashboard-welcome">
            <div className="welcome-card">
              <h2>Welcome back, {currentUser.firstName}! ðŸ‘‹</h2>
              <p>
                {currentUser.role === EmployeeRole.HR 
                  ? "Ready to help create meaningful connections and support our newcomers?"
                  : currentUser.isBuddyGuide 
                    ? "Ready to mentor and share your expertise with newcomers?"
                    : "Welcome to the team! Let's get you connected with a great buddy guide."
                }
              </p>
              <button 
                className="btn btn-sm btn-outline"
                onClick={() => setCurrentUser(null)}
              >
                Switch User
              </button>
            </div>
          </div>

          <StatsCards 
            currentUser={currentUser} 
            employees={employees}
          />

          <div className="dashboard-grid">
            <div className="dashboard-column">
              <QuickActions currentUser={currentUser} />
            </div>
            <div className="dashboard-column">
              <RecentActivity currentUser={currentUser} />
            </div>
          </div>
        </div>
      )}
    </div>
  );
};

export default Dashboard;
